mapper.select:
SELECT sp.purchase_id, sp.purchase_no, sp.goods_name, sp.goods_band, sp.goods_type, sp.apply_quantity, sp.goods_unit, sp.budget, sp.apply_remark, applicant.user_id applicant_id, applicant.username applicant_name, sp.apply_time, ss.status_id, ss.status_code, ss.status_text FROM sys_purchase sp JOIN sys_user applicant ON sp.applicant_id=applicant.user_id LEFT JOIN sys_user reviewer ON sp.reviewer_id=reviewer.user_id LEFT JOIN sys_user purchaser ON sp.purchaser_id=purchaser.user_id LEFT JOIN sys_user store_reviewer ON sp.store_reviewer_id=store_reviewer.user_id LEFT JOIN sys_user receiver ON sp.receiver_id=receiver.user_id JOIN sys_status ss ON sp.status_code=ss.status_code
sp.status_code=#{statusCode} and
ORDER BY sp.apply_time DESC LIMIT #{begin}, #{size}

mapper.update:
update sys_purchase set
review_remark=#{reviewRemark}, reviewer_id=#{reviewer.userId}, review_time=#{reviewTime}, status_code=#{status.statusCode}
where purchase_id=#{purchaseId}
